'use strict';

module.exports = {
  forgot_password_email: function(user, req, token, mailOptions) {
    mailOptions.html = [
      'Hi ' + user.username + ',',
      'We have received a request to reset the password for your account.',
      'If you made this request, please click on the link below or paste this into your browser to complete the process:',
      'http://' + req.headers.host + '/reset/' + token,
      'This link will work for 1 hour or until your password is reset.',
      'If you did not ask to change your password, please ignore this email and your account will remain unchanged.'
    ].join('\n\n');
    mailOptions.subject = 'Resetting the password';
    return mailOptions;
  },
  email_confirmation: function(user, req, token, mailOptions) {
    mailOptions.html = [
      'Hi ' + user.username + ',\n\n',
      'Thank you for registering.\n\n',
      'Please click on the link below or paste this into your browser to activate your account:\n\n',
      'http://' + req.headers.host + '/api/confirm/' + token,
      '\n\nThis link will work until your account is active.\n\n'
    ].join('\n\n');
    mailOptions.subject = 'Required Email Confirmation';
    return mailOptions;
  }
};
